DROP VIEW IF EXISTS `view_contrato`;
CREATE ALGORITHM=UNDEFINED DEFINER=`sicdev`@`localhost` SQL SECURITY DEFINER VIEW `view_contrato`  AS SELECT `contrato`.`id` AS `contrato_id`, `contrato`.`cliente_id` AS `cliente_id`, `contrato`.`tipo_contrato_id` AS `tipo_contrato_id`, `contrato`.`dt_inicio` AS `dt_inicio`, `contrato`.`dt_fim` AS `dt_fim`, `tipo_contrato`.`nome` AS `tipo_contrato`, `pessoa`.`nome_fantasia` AS `nome_cliente`, `cidade`.`nome` AS `cidade`, `estado`.`uf` AS `uf`, `estado`.`nome` AS `nome_estado`, `grupo`.`nome` AS `nome_grupo`, `contrato`.`ativo` AS `ativo`, `contrato`.`valor` AS `total` FROM (((((`contrato` join `tipo_contrato`) join `pessoa`) join `cidade`) join `estado`) join `grupo`) WHERE `contrato`.`tipo_contrato_id` = `tipo_contrato`.`id` AND `contrato`.`cliente_id` = `pessoa`.`id` AND `pessoa`.`cidade_id` = `cidade`.`id` AND `cidade`.`estado_id` = `estado`.`id` AND `pessoa`.`grupo_id` = `grupo`.`id` ;


